String paramToken = (String) request.getParameter("csrfToken");
String sessToken = (String) ses.getAttribute("csrfToken");
if (paramToken.compareTo(sessToken) == 0) {
  log.debug("Getting Registration Parameters");
  String userName = (String) request.getParameter("userName");
  log.debug("userName = " + userName);
  String passWord = (String) request.getParameter("passWord");
  log.debug("passWord retrieved");
  String passWordConfirm = (String) request.getParameter("passWordConfirm");
  log.debug("passWordConfirm retrieved");
  String userAddress = (String) request.getParameter("userAddress");
  log.debug("userAddress = " + userAddress);
  String userAddressCnf = (String) request.getParameter("userAddressCnf");
  log.debug("userAddressCnf = " + userAddressCnf);

  // Validation
  log.debug("Checking for nulls");
  notNull = (userName != null && passWord != null);
  log.debug("Ensuring strings are not empty");
  notEmpty = (!userName.isEmpty() && !passWord.isEmpty());
  log.debug("Validating passwords");
  validPasswords = passWord.compareTo(passWordConfirm) == 0; // 0 returned if the same
  log.debug("Validating addresses");
  validAddress = userAddress.compareTo(userAddressCnf) == 0;
  validAddress = (Validate.isValidEmailAddress(userAddress) && validAddress);
  if (!validAddress) {
    userAddress = new String();
  }
  boolean basicValidation = validPasswords && notNull && notEmpty;
  if (basicValidation && !validAddress) {
    userValidate = (Validate.isValidUser(userName, passWord));
  } else {
    userValidate = (Validate.isValidUser(userName, passWord, userAddress));
  }
  if (basicValidation && userValidate) {
    // Data is good, Add user
    // Any Class Set to Add them to?
    if (!isLoaded) {
      try {
        defaultClass = Getter.getDefaultClass("");
        isLoaded = true;
      } catch (SQLException e) {
        log.fatal("Could not load default class: " + e.toString());
        throw new RuntimeException(e);
      }
    }
    if (defaultClass.isEmpty()) {
      log.debug("Adding player to database, with null classId");
      Setter.userCreate(
          ApplicationRoot, null, userName, passWord, "player", userAddress, false);
    } else // defaultClass is not empty, so It must be set to a class!
    {
      log.debug("Adding player to database, to class " + defaultClass);
      Setter.userCreate(
          ApplicationRoot, defaultClass, userName, passWord, "player", userAddress, false);
    }
    response.sendRedirect("login.jsp");
  } else {
    // Validation Error Responses
    if (!notNull || !notEmpty) {
      log.error("Null values detected");
      ses.setAttribute("errorMessage", "Invalid Request. Please try again");
    } else if (!validPasswords) {
      log.error("Passwords did not match");
      ses.setAttribute("errorMessage", "Password fields did not match");
    } else if (!validAddress) {
      log.error("Invalid Addresses Detected");
      ses.setAttribute("errorMessage", "Invalid Request. Please try again");
    } else if (!userValidate) {
      log.error("Invalid Addresses Detected");
      ses.setAttribute("errorMessage", "");
    }
    response.sendRedirect("register.jsp");
  }
} else {
  log.debug("paramToken = " + paramToken);
  log.debug("sessToken = " + sessToken);
}

String dbAuth = request.getParameter("dbauth");

String mongodbHost = request.getParameter("mhost");
String mongodbPort = request.getParameter("mport");
String nosqlprops =
    new File(Database.class.getResource("/challenges/NoSqlInjection1.properties").getFile())
        .getAbsolutePath();

try (InputStream mongo_input = new FileInputStream(nosqlprops)) {

  mongo_props.load(mongo_input);
}

String mongodbName = mongo_props.getProperty("databaseName");
if (mongodbName == null) {
  String message = "Could not find databaseName in nosql properties file";
  log.fatal(message);
  throw new RuntimeException(message);
}

log.debug("Starting database setup...");

String auth = "";

String enableMongoChallenge = request.getParameter("enableMongoChallenge");

String enableUnsafeLevels = request.getParameter("unsafeLevels");

// Mongo DB properties
StringBuffer mongoProp = new StringBuffer();
mongoProp.append("connectionHost=" + mongodbHost);
mongoProp.append("\n");
mongoProp.append("connectionPort=" + mongodbPort);
mongoProp.append("\n");
mongoProp.append("databaseName=" + mongodbName);
mongoProp.append("\n");
mongoProp.append("connectTimeout=10000");
mongoProp.append("\n");
mongoProp.append("socketTimeout=0");
mongoProp.append("\n");
mongoProp.append("serverSelectionTimeout=30000");
mongoProp.append("\n");

try {
  auth = new String(Files.readAllBytes(Paths.get(Constants.SETUP_AUTH)));
} catch (NoSuchFileException e) {
  // Auth file could not be found.
  htmlOutput += "Auth file could not be found";
  log.error("Auth file could not be found: " + e.toString());
}

if (auth == "") {
  // No auth loaded, could be because user never reloaded setup page after an
  // error. Generate it again
  log.debug("Generating auth file");

  generateAuth();
}

if (!auth.equals(dbAuth)) {
  log.debug("Invalid auth supplied");

  // The supplied auth data was incorrect
  htmlOutput += bundle.getString("generic.text.setup.authentication.failed");
  log.error("Authorization mismatch: " + auth + " does not equal " + dbAuth);

} else {
  // Test the user's entered database properties
  Boolean connectionSuccess = false;
  log.debug("Attempting to connect to database");

  try {
    Connection conn =
        Database.getConnection(driverType, connectionURL, dbOptions, dbUser, dbPass);
    Database.closeConnection(conn);
    connectionSuccess = true;
    log.debug("Database connection successful");

  } catch (SQLException e) {
    htmlOutput += bundle.getString("generic.text.setup.connection.failed") + e.getMessage();

    log.error("DB connection error: " + e.toString());
    connectionSuccess = false;
  }

  if (!connectionSuccess) {
    htmlOutput += bundle.getString("generic.text.setup.connection.failed");
  } else {
    // Write the user's entered mysql database properties to file

    if (saveMysqlProperties) {

      try (OutputStream mysql_output = new FileOutputStream(Constants.MYSQL_DB_PROP)) {

        mysql_props = new Properties();

        mysql_props.setProperty("databaseConnectionURL", connectionURL);
        mysql_props.setProperty("DriverType", driverType);
        mysql_props.setProperty("databaseOptions", dbOptions);
        mysql_props.setProperty("databaseSchema", "core");
        mysql_props.setProperty("databaseUsername", dbUser);
        mysql_props.setProperty("databasePassword", dbPass);

        // save properties to project root folder
        mysql_props.store(mysql_output, null);
        success = true;

      } catch (IOException e) {

        success = false;

        htmlOutput = bundle.getString("generic.text.setup.failed") + ": " + e.getMessage();

        log.error("Could not save mysql properties file: " + e.toString());
      }

    } else {
      success = true;
    }

    if (success) {
      // Writing db file succeeded

      try {
        if (dbOverride.equalsIgnoreCase("override")) {
          executeSqlScript();
          htmlOutput =
              bundle.getString("generic.text.setup.success")
                  + " "
                  + bundle.getString("generic.text.setup.success.overwrittendb");
        } else if (dbOverride.equalsIgnoreCase("upgrade")) {
          executeUpdateScript();
          htmlOutput =
              bundle.getString("generic.text.setup.success")
                  + " "
                  + bundle.getString("generic.text.setup.success.updatedb");
        } else {
          htmlOutput = bundle.getString("generic.text.setup.success");
        }
        success = true;
      } catch (SQLException e) {
        htmlOutput = bundle.getString("generic.text.setup.failed") + ": " + e.getMessage();
        log.error(bundle.getString("generic.text.setup.failed") + ": " + e.getMessage());
        if (!hasDBFile) {
          FileUtils.deleteQuietly(new File(Constants.MYSQL_DB_PROP));
        }
      }
      // Clean up File as it is not needed anymore. Will Cause a new one to be
      // generated next time too
      removeAuthFile();
    }

    if (enableMongoChallenge.equalsIgnoreCase("enable")) {
      if (!Validate.isValidPortNumber(mongodbPort)) {
        htmlOutput = bundle.getString("generic.text.setup.error.valid.port");
        FileUtils.deleteQuietly(new File(Constants.MYSQL_DB_PROP));
      } else {
        Files.write(
            Paths.get(Constants.MONGO_DB_PROP),
            mongoProp.toString().getBytes(),
            StandardOpenOption.CREATE);
        if (MongoDatabase.getMongoDbConnection(null).listDatabaseNames() == null) {
          htmlOutput = bundle.getString("generic.text.setup.connection.mongo.failed");
          if (!hasDBFile) {
            FileUtils.deleteQuietly(new File(Constants.MYSQL_DB_PROP));
          }
        } else {
          try {
            executeMongoScript();
          } catch (IOException e) {
            htmlOutput =
                bundle.getString("generic.text.setup.failed") + ": " + e.getMessage();
            if (!hasDBFile) {
              FileUtils.deleteQuietly(new File(Constants.MYSQL_DB_PROP));
            }
          }
        }
      }
    }

    if (enableUnsafeLevels.equalsIgnoreCase("enable")) {
      openUnsafeLevels();
      if (!executeCreateChallengeFile()) {
        htmlOutput = bundle.getString("generic.text.setup.file.failed");
        if (!hasDBFile) {
          FileUtils.deleteQuietly(new File(Constants.MYSQL_DB_PROP));
        }
      }
    }
  }
}

String classId = (String) request.getParameter("classId");
log.debug("classId = " + classId);
String scoreboardMessage = new String();
if (classId
    .isEmpty()) // Null Submitted - configure scoreboard to list all players regardless of
// class
{
  log.debug("Null Class submitted");

  log.debug("Unpacking restricted scoreboard value");
  String restrictedScoreboard =
      Validate.validateParameter(request.getParameter("restricted"), 5);

  if (restrictedScoreboard.isEmpty()) {

    ScoreboardStatus.setScoreboardOpen();
    scoreboardMessage =
        "Scoreboard is now enabled and lists all users regardless of their class.";
  } else {
    log.debug("Restricted scoreboard value found");

    boolean isAdminOnly = restrictedScoreboard.equals("true");

    if (!isAdminOnly) {
      // Scoreboard is public
      ScoreboardStatus.setScoreboardPublic();
      log.debug("Public Scoreboard Enabled");
      scoreboardMessage = "Scoreboard is now enabled for public view.";

      htmlOutput =
          "<h3 class='title'>Scoreboard Settings Updated</h3>"
              + "<p>"
              + scoreboardMessage
              + " The scoreboard is public</p>";
    } else {
      // Scoreboard is admin only
      ScoreboardStatus.setScoreboardPublic();
      log.debug("Admin only scoreboard set");
      scoreboardMessage = "Scoreboard is only enabled for administrators.";

      htmlOutput =
          "<h3 class='title'>Scoreboard Settings Updated</h3>"
              + "<p>"
              + scoreboardMessage
              + " The scoreboard is admin only</p>";
    }
  }

} else if (classId.equalsIgnoreCase("classSpecific")) {
  // Set Class Specific Scoreboards

  ScoreboardStatus.setScoreboardClassSpecific();
  scoreboardMessage =
      "Scoreboard has been enabled and only lists users from the viewer's class. Admin"
          + " users will still see the scoreboard of the default class.";
  log.debug(scoreboardMessage);

} else {
  // validate class identifier
  classInfo = Getter.getClassInfo(applicationRoot, classId);
  if (classInfo != null && !classInfo[0].isEmpty()) // Class Exists
  {
    log.debug("Valid Class Submitted, setting scoreboard class to " + classId);
    ScoreboardStatus.setScoreboardClass(classId);
    scoreboardMessage =
        "Scoreboard has been enabled and only lists users from "
            + Encode.forHtml(classInfo[0]);
  }
}

